<script>
import { mapActions, mapGetters } from "vuex";
import superadmin from "./superadmin.vue";
import allusers from "../components/allusers.vue";
import reports from "../components/reports.vue";
import providersTripsDash from "../components/providersTripsDash.vue";
import createTrip from "../components/createTrip.vue";
import bookedTripsProvideDash from "../components/bookedTripsProvideDash.vue";
import userDash from "../components/userDash.vue";
import userUpcomingTrips from "../components/userUpcomingTrips.vue";
import userPastTrips from "../components/userPastTrips.vue";
import axios from "axios";

export default {
  components: {
    superadmin,
    allusers,
    providersTripsDash,
    createTrip,
    bookedTripsProvideDash,
    userDash,
    userUpcomingTrips,
    userPastTrips,
    reports,
  },
  name: "ProfilePage",
  data() {
    return {
      showSuperAdmin: false,
      showallusers: false,
      showProvidersTripsDash: false,
      showCreateTrip: false,
      showBookedTrips: false,
      showUserTrips: false,
      showUserUpcomingTrips: false,
      showUserPastTrips: false,
      activeBtn: null,
      showTrips: true,
      showReports: false,
    };
  },
  computed: {
    ...mapGetters(["getUser"]),
    user() {
      console.log(this.getUser);
      return this.getUser;
    },
  },
  methods: {
    ...mapActions(["clearUser"]),
    selectSuperAdmin() {
      this.showSuperAdmin = !this.showSuperAdmin;
      this.showallusers = false;
      this.showReports = false;
      this.activeBtn = "superadmin";
    },
    selectAllUsers() {
      this.showallusers = !this.showallusers;
      this.showSuperAdmin = false;
      this.showReports = false;
      this.activeBtn = "allusers";
    },
    selectReports() {
      this.showReports = !this.showReports;
      this.showSuperAdmin = false;
      this.showallusers = false;
      this.activeBtn = "reports";
    },
    selectProvidersTripsDash() {
      this.showProvidersTripsDash = !this.showProvidersTripsDash;
      this.showCreateTrip = false;
      this.showBookedTrips = false;
      this.activeBtn = "providertrips";
    },
    selectCreateTrip() {
      this.showCreateTrip = !this.showCreateTrip;
      this.showProvidersTripsDash = false;
      this.showBookedTrips = false;
      this.activeBtn = "createtrip";
    },
    selectBookedTrips() {
      this.showBookedTrips = !this.showBookedTrips;
      this.showCreateTrip = false;
      this.showProvidersTripsDash = false;
      this.activeBtn = "bookedtrips";
    },
    selectUserData() {
      this.showUserTrips = !this.showUserTrips;
      this.showUserUpcomingTrips = false;
      this.showUserPastTrips = false;
      this.activeBtn = "mytrips";
    },
    selectUserUpcomingTrips() {
      this.showUserUpcomingTrips = !this.showUserUpcomingTrips;
      this.showUserTrips = false;
      this.showUserPastTrips = false;
      this.activeBtn = "upcomingtrips";
    },
    selectUserPastTrips() {
      this.showUserPastTrips = !this.showUserPastTrips;
      this.showUserTrips = false;
      this.showUserUpcomingTrips = false;
      this.activeBtn = "pasttrips";
    },
    logout() {
      this.clearUser();
      this.$router.push("/login");
    },
    activeAccount(user) {
      const updatedVis = this.showTrips;
      axios
        .patch(
          `https://dqorskomhqezaztrdhzc.supabase.co/rest/v1/users?id=eq.${user.id}`,
          { vis: updatedVis },
          {
            headers: {
              apikey:
                "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImRxb3Jza29taHFlemF6dHJkaHpjIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MjQ4NjA0ODUsImV4cCI6MjA0MDQzNjQ4NX0.sT1ehQrr-wQuwq0OVRgDtPh2gnvRGfKIMiDXlZIuvMI",
              Authorization:
                "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImRxb3Jza29taHFlemF6dHJkaHpjIiwicm9sZSI6InNlcnZpY2Vfcm9sZSIsImlhdCI6MTcyNDg2MDQ4NSwiZXhwIjoyMDQwNDM2NDg1fQ.2VhaDTcy4Cuyrek0tdur92cXYr1zeIHMm0yMti4Kkn8",
              "Content-Type": "application/json",
              Prefer: "return=representation",
            },
          }
        )
        .then((response) => {
          user.vis = updatedVis;

          console.log(response);
        })
        .catch((error) => {
          console.error(error);
        });
    },
    fetchUser() {
      axios
        .get(
          `https://dqorskomhqezaztrdhzc.supabase.co/rest/v1/users?id=eq.${this.user.id}`,
          {
            headers: {
              apikey:
                "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImRxb3Jza29taHFlemF6dHJkaHpjIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MjQ4NjA0ODUsImV4cCI6MjA0MDQzNjQ4NX0.sT1ehQrr-wQuwq0OVRgDtPh2gnvRGfKIMiDXlZIuvMI",
              Authorization:
                "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImRxb3Jza29taHFlemF6dHJkaHpjIiwicm9sZSI6InNlcnZpY2Vfcm9sZSIsImlhdCI6MTcyNDg2MDQ4NSwiZXhwIjoyMDQwNDM2NDg1fQ.2VhaDTcy4Cuyrek0tdur92cXYr1zeIHMm0yMti4Kkn8",
              "Content-Type": "application/json",
              Prefer: "return=representation",
            },
          }
        )
        .then((response) => {
          // Assuming response.data is an array with one user object
          const userData = response.data[0];
          this.user = userData;
          this.showTrips = userData.vis; // Initialize toggle with vis value
        })
        .catch((error) => {
          console.error("Error fetching user data:", error);
        });
    },
  },
  mounted() {
    this.fetchUser();
  },
