    <form
      @submit.prevent="addAccount"
      class="w-full lg:w-6/12 bg-[#ffe7bb] flex flex-col p-8 rounded-2xl my-10 lg:my-0"
    >
      <div class="logo flex flex-row items-center mb-8">
        <router-link to="/home" class="cursor-pointer">
        <img src="../assets/egypt (2).png" alt="" width="48px" />
        </router-link>
        <div class="title">
          <h1 class="text-3xl font-bold text-[#ffb830]">Trip It</h1>
        </div>
      </div>
      <div class="form_title">
        <h1 class="text-2xl font-semibold text-[#ffb830] mb-5"> {{$t('sign')}}</h1>
      </div>
      <div class="form_input flex flex-col">
        <label for="username" class="text-[#ffb830] font-bold mt-4"
          >{{$t('userName')}}</label
        >
        <input
          type="text"
          class="w-full bg-slate-50 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#Ffb830]"
          v-model="username"
          placeholder="Enter your username"
          required
        />
        <label for="password" class="text-[#ffb830] font-bold mt-4"
          >{{$t('pass')}}</label
        >
        <input
          type="password"
          class="w-full bg-slate-50 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#Ffb830]"
          v-model="password"
          placeholder="********"
          required
        />
        <label for="email" class="text-[#ffb830] font-bold mt-4">{{$t('email')}}</label>
        <input
          type="email"
          class="w-full bg-slate-50 p-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#Ffb830]"
          v-model="email"
          placeholder="username@mail.com"
          required
        />
        <div class="radio_inputs w-full flex flex-col justify-between">
          <div class="gender_inputs flex flex-row mt-4">
            <input
              class="mr-2 accent-[#ffb830] outline-accent-[#ffb830]"
              type="radio"
              id="male"
              name="gender"
              value="male"
              v-model="gender"
            />
            <label class="mr-2 text-[#ffb830]" for="male">{{$t('male')}}</label>
            <input
              class="mr-2 accent-[#ffb830] outline-accent-[#ffb830]"
              type="radio"
